import React from 'react'
import { useState } from 'react';

export const AddTask = ({taskList, setTaskList}) => {
    let [count, setCount] = useState(0);
    let [taskValue, setTaskValue] = useState("");
    let setTasks = () => {
        props.setTaskList([{id: {count}, name: taskValue, time: "x"}]);
        setCount(count + 1);
        setTaskValue("");
    }

    const handleImport = (event) => {
        setTaskValue(event.target.value);
    }

    

  return (
    <section className="addTask">
        <form>
            <input onChange={handleImport} type="text" name="task"  autoComplete='off' placeholder='add task' maxLength={20} value={taskValue}/>
            <button onClick={setTasks()}>Add</button>
        </form>

    </section>
  )
}

export default AddTask;
