import { useState } from 'react';
import AddTask from './components/AddTask';
import ShowTask from './components/ShowTask';

function App() {
  let [taskList, setTaskList] = useState([]);

  let [styles, setStyles] = useState("");

  return (
    <div className="App">
      <header>
        <div className="logo">
            <img src={logo} alt="logo of the application" />
            <span>Task Management</span>
        </div>
        <div className="themeSelector">
            <span onClick={() => lightTheme} className="light activeTheme"></span>
            <span onClick={() => mediumTheme} className="medium"></span>
            <span onClick={() => darkTheme} className="dark"></span> 
        </div>
    </header>
      <AddTask taskList={taskList} setTaskList={setTaskList} />
      <ShowTask key={taskList.id} taskList={taskList} setTaskList={setTaskList} />
    </div>
  );
}

export default App;
