import React from 'react'

export const ShowTask = ({taskList, setTaskList}) => {
    const tasks = [
        {id: 10001, name: "Washing clothes", time: "2:09:01 AM 9/14/2023"},
        {id: 10002, name: "Study", time: "2:09:02 AM 9/14/2023"},
        {id: 10003, name: "Workout", time: "2:09:03 AM 9/14/2023"}
    ];
  return (
    <section className='showTask'>
        <div className="head">
            <div>
                <span className='title'>Todo</span>
                <span className='count'>0</span>
                <button className='clearAll right'>Clear all</button>
            </div>
        </div>
        <ul>
            { tasks.map((task) => (
            <li>
                <p>
                    <span className='name'>{task.name}</span>
                    <span className='time'>{task.time}</span>
                </p>
                <i className='bi bi-trash3'></i>
            </li>
            ))}
            
            
            
        </ul>
    </section>
  )
}


export default ShowTask;
