import React from 'react'
import logo from "../assets/Header.png"

export const Header = ({styles, setStyles}) => {

   const lightTheme = () => {
        const style = "background-color: white";
        setStyles(style);
   }

   const mediumTheme = () => {
        const style = "background-color: gray";
        setStyles(style);
   }

   const darkTheme = () => {
    const style = "background-color: black";
    setStyles(style);
   }
    
  return (
    <header>
        <div className="logo">
            <img src={logo} alt="logo of the application" />
            <span>Task Management</span>
        </div>
        <div className="themeSelector">
            <span onClick={() => lightTheme} className="light activeTheme"></span>
            <span onClick={() => mediumTheme} className="medium"></span>
            <span onClick={() => darkTheme} className="dark"></span> 
        </div>
    </header>
  )
}


export default Header
